import React, { useState } from "react";
import { Calendari } from "./style";


function marcarDia(element: any, setDia: any) {
  setDia(element);
}


function OrganizarSemana(diaSemana: any, props: any, setCor: any, cor: any) {
  let domingo: any = [];

  diaSemana.forEach((element: string) => {
    let corr= "white";
    if(element == props.dia){
      setCor("red");
      corr=cor;
    }
    domingo.push(<li
      className={element}
      style={{ background: corr }}
      onClick={() => marcarDia(element, props.setDia)}
    >{element}</li>);
  });

  return domingo;
}

export default function Calendario(props: any) {
  const [cor, setCor] = useState("white");
  let domingo, segunda, terca, quarta, quinta, sexta, sabado: any;
  useEffect(() => {
    domingo = OrganizarSemana(props.dias[0], props, setCor, cor);
    segunda = OrganizarSemana(props.dias[1], props, setCor, cor);
    terca = OrganizarSemana(props.dias[2], props, setCor, cor);
    quarta = OrganizarSemana(props.dias[3], props, setCor, cor);
    quinta = OrganizarSemana(props.dias[4], props, setCor, cor);
    sexta = OrganizarSemana(props.dias[5], props, setCor, cor);
    sabado = OrganizarSemana(props.dias[6], props, setCor, cor);
  }, [cor]);


  return (
    <>
      <Calendari>
        <ul className="diasSemana" >
          <li>Dom</li>
          <li>Seg</li>
          <li>Ter</li>
          <li>Qua</li>
          <li>Qui</li>
          <li>Sex</li>
          <li>Sab</li>
        </ul>
        <div className="mes" >
          <ul>
            {domingo}
          </ul>
          <ul>
            {segunda}
          </ul>
          <ul>
            {terca}
          </ul>
          <ul>
            {quarta}
          </ul>
          <ul>
            {quinta}
          </ul>
          <ul>
            {sexta}
          </ul>
          <ul>
            {sabado}
          </ul>
        </div>
      </Calendari>
    </>
  );


}
function setDia(element: any) {
  throw new Error("Function not implemented.");
}
