import React from 'react';
import Card from './components/card/Card';
import './global.ts';
import { Container } from './styles';
import { GlobalStyle } from "./global";


function App() {
  let data = new Date();
  let mesAtual = data.getMonth() + 2;
  let diaAtual = data.getDay();
  let anoAtual = data.getFullYear();

  let diasDoMes = new Date(anoAtual, mesAtual, 0).getDate();

  let dadosCard: any = []

  dadosCard[0] = [];
  dadosCard[1] = [];
  dadosCard[2] = [];
  dadosCard[3] = [];
  dadosCard[4] = [];
  dadosCard[5] = [];
  dadosCard[6] = [];

  for (let i = 1; i <= diasDoMes; i++) {
    if (0 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[0].push(i);
    }
    if (1 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[1].push(i);
    }
    if (2 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[2].push(i);
    }
    if (3 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[3].push(i);
    }
    if (4 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[4].push(i);
    }
    if (5 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[5].push(i);
    }
    if (6 == new Date(anoAtual, mesAtual - 1, i).getDay()) {
      dadosCard[6].push(i);
    }
  }
  for (let i = 0; i <= 7; i++) {
    let xs = new Date(anoAtual, mesAtual - 1, 0).getDay();
    if (i <= xs) {
      console.log(i);
      dadosCard[i].unshift('x');
    }
  }

  let card = <Card
    dias={dadosCard}
  ></Card>;
  return (
    <Container>
      {card}
      <GlobalStyle />
    </Container>
  );
}
export default App;
