import React from 'react';
import Card from './components/card/Card';
import './global.ts';
import { Container } from './styles';
import { GlobalStyle } from "./global";


function App() {
  let data = new Date();
  let mesAtual = data.getMonth()+1;
  let diaAtual = data.getDay();
  let anoAtual = data.getFullYear();

  let diasDoMes = new Date(anoAtual, mesAtual, 0).getDate();

  let dadosCard = {
    domingo : [1,2,3,4,5]
    ,segunda : [1,2,3,4,5]
    ,terca : [1,2,3,4,5]
    ,quarta : [1,2,3,4,5]
    ,quinta : [1,2,3,4,5]
    ,sexta : [1,2,3,4,5]
    ,sabado : [1,2,3,4,5]
  };

  for (let i = 1; i <= diasDoMes; i++) {
    if(1 == new Date(anoAtual, mesAtual-1, i).getDay()){
      console.log(i+" -- ");
      domingo[i]=i;
      segunda[i]=i;
      terca[i]=i;
      quarta[i]=i;
      quinta[i]=i;
      sexta[i]=i;
      sabado[i]=i;
    }

  }


  console.log(diasDoMes);
  //console.log(diasd);

  let card = <Card
    dias={dadosCard}
  ></Card>;
  return (
    <Container>
       {card}
      <GlobalStyle />
    </Container>
  );
}
export default App;
